func findTheDifference1(s string, t string) byte {
	defer timeCost()()
	sbyte, tbyte := []byte(s), []byte(t)
	length := len(sbyte)
	mp := make(map[byte]int)
	mp[tbyte[length]]--
	for i := 0; i < length; i++ {
		mp[sbyte[i]]++
		mp[tbyte[i]]--
	}

	for k, v := range mp {
		if v < 0 {
			return k
		}
	}
	panic("panic panic panic")
}

func findTheDifference2(s string, t string) byte {
	defer timeCost()()
	length := len(s)
	list := make([]int, 26)
	list[t[length]-97]--
	for i := 0; i < length; i++ {
		list[s[i]-97]++
		list[t[i]-97]--
	}

	for k, v := range list {
		if v < 0 {
			return byte(k + 97)
		}
	}
	panic("panic panic panic")
}

func findTheDifference(s string, t string) byte {
	defer timeCost()()
	length := len(s)
	sum := t[length]
	for i := 0; i < length; i++ {
		sum = sum + (t[i] - s[i])
	}
	return sum
}
